import React, { Component } from 'react';
import './index.css'



class Form extends Component {
    constructor(){
        super()
        this.state ={
            username : '',
            comments : '',
            choose : ''
        }
        this.handleChange = this.handleChange.bind(this)
        this.handleComment = this.handleComment.bind(this)
        this.handleChoose = this.handleChoose.bind(this)
    }
    
    handleChange(event){
        this.setState ({
            username : event.target.value             
        })
    }
    handleComment(event){
        this.setState({
            comments : event.target.value
        })
    }
    handleChoose(event){
        this.setState({
            choose : event.target.value
        })
    }
    render() { 
        return (
            <div>
               <form>
                   <div className='input'>
                        <label>username</label>
                        <input type='text' value ={this.state.username} onChange ={this.handleChange} />
                   </div>
                   <div className='input'>
                        <label>comments</label>
                        <input type='text' value ={this.state.comments} onChange ={this.handleComment} />
                   </div>
                   <div className='input'>
                        <label>choose</label>
                        <input type='text' value ={this.state.choose} onChange ={this.handleChoose} />
                   </div>
               </form>
            </div>
          );
    }
}
 
export default Form ;
